    public function run(): void
    {
        foreach($this->categories as $item){
            if(!Category::where('name',$item[1])->first()){
                Category::create([
                    'name' => $item[1]
                ]);
            };
        };
        foreach($this->books as $item){
          if(!Book::where('name',$item[1])->first()){
            Book::create([
                'name' => $item[1],
                'image' => $item[2],
                'description' => $item[4],
                'price' => $item[3]
            ]);
            foreach($item[5] as $i){
                category_book::create([
                    'book_id' => $item[0],
                    'category_id' => $i
                ]);
            };
          };
        };

    }
}
